import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Square(props) {
  return (
    <button className="square" onClick={props.handleClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      board:Array(9).fill(2),
      shown: Array(9).fill(false),
      xval:null,
      yval:null
    };
  }
  generateSquare(value){
    return (<Square
      value = {this.state.shown[value] ? this.props.board[value] : null}
      handleClick = {(value) => this.handleClick(value)}
    />);
  }
  handleClick(val){
    const curr = this.state.shown.slice()
    curr[val] = true
    this.setState(
      {
        shown: curr,
      }
    )
  }
  generateBoard(val1, val2){
    var list = [];
    for (var i = 0; i <= val1; i++) {
      list.push(i);
    }
    var list2 = [];
    for (var i = 0; i <= val2; i++){
      list2.push(i)
    }
    return(
      list2.map((m) => {return(
          <div className = "board-row">
            {list.map((n) => {return this.generateSquare(m*(this.state.xval+1)+n);})}
          </div>
        )}
      )
      )
  }
  render(){
    return(
      this.generateBoard(this.props.xval,this.props.yval)
    );
  }
}

class Game extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      xval:2,
      yval:2,
      history : [{
        shown: Array(3*3).fill(null),
      }],
      gameboard: Array(3*3).fill(null),
      index:0
    };
  }
  renderBoard(){
    return(
      <Board
        xval = {this.state.xval}
        yval = {this.state.yval}
        board = {Array(9).fill(2)}
      />
    );
  }
  render(){
    return(
      this.renderBoard()
    );
  }
}
function App() {
  return (
    <div className="App">
      <Game/>
    </div>
  );
}

export default App;
