import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Square extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      value:null,
    };
  }
  render(){
    return(
      <button className="square" onClick = {() => this.setState({value:1,})}>
        {this.state.value}
      </button>
    );
  }
}

class Board extends React.Component{
  generateSquare(value){
    return (<Square
      value = {value}
    />);
  }
  generateBoard(val1, val2){
    this.xdim = val1
    this.ydim = val2
    this.squareRow = []
    return(
    [1,2,3].map ( (n) => {
      return this.generateSquare(0);
    })
    )
  }
  render(){
    return(
      <div>
        <div className="board-row">
          {this.generateBoard(0,0)}
        </div>
        <div className="board-row">
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
        </div>
        <div className="board-row">
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
          {this.generateSquare(0)}
      </div>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Board/>
    </div>
  );
}

export default App;
