import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Square extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      value:null,
    };
  }
  render(){
    return(
      <button className="square" onClick = {() => this.setState({value:this.props.value,})}>
        {this.state.value}
      </button>
    );
  }
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      shown:[],
      xval:null,
      yval:null
    };
  }
  generateSquare(value){
    return (<Square
      value = {value}
    />);
  }
  generateBoard(val1, val2){
    var list = [];
    for (var i = 0; i <= val1; i++) {
      list.push(i);
    }
    var list2 = [];
    for (var i = 0; i <= val2; i++){
      list2.push(i)
    }
    return(
      list2.map((m) => {return(
          <div className = "board-row">
            {list.map((n) => {return this.generateSquare(m*(val1+1)+n);})}
          </div>
        )}
      )
      )
  }
  render(){
    return(
      this.generateBoard(this.props.xval,this.props.yval)
    );
  }
}

class Game extends React.Component{
  renderBoard(){
    return(
      <Board
        xval = {2}
        yval = {2}
      />
    );
  }
  render(){
    return(
      this.renderBoard()
    );
  }
}
function App() {
  return (
    <div className="App">
      <Game/>
    </div>
  );
}

export default App;
