import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Square(props) {
  return (
    <button className="square" onClick={props.handleClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      board: this.props.gameboard,
      shown: Array(this.props.xval * this.props.yval).fill(false),
    };
  }
  generateSquare(value){
    return (<Square
      value = {this.state.shown[value] ? this.props.board[value] : null}
      handleClick = {() => this.handleClick(value)}
    />);
  }
  handleClick(val){
    const curr = this.state.shown.slice()
    curr[val] = true
    console.log(curr)
    this.setState(
      {
        board:this.props.gameboard,
        shown: curr,
      }
    );
  }
  generateBoard(val1, val2){
    var list = [];
    for (var i = 0; i <= val1-1; i++) {
      list.push(i);
    }
    var list2 = [];
    for (var x = 0; x <= val2-1; x++){
      list2.push(x)
    }
    return(
      list2.map((m) => {return(
          <div className = "board-row">
            {list.map((n) => {return this.generateSquare(m*(val1)+n);})}
          </div>
        )}
      )
      )
  }
  render(){
    return(
      this.generateBoard(this.props.xval,this.props.yval)
    );
  }
}

class Game extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      xval:this.props.xval,
      yval:this.props.yval,
      gameboard: this.generateRandomBoard(),
      index:0
    };
  }
  generateRandomBoard(){
    var board = Array(this.props.xval*this.props.yval).fill(0)
    getRandomInt(5, 5 + Math.floor((this.props.xval*this.props.yval)/2))
    var bombs = 20
    while(bombs > 0){
      const cRow = getRandomInt(0, this.props.xval)
      const cCol = getRandomInt(0, this.props.yval)
      const ind = cRow*this.props.xval + cCol
      if(board[ind] !== 'B'){
        board[ind] = 'B'
        bombs -= 1;
      }
    }
    console.log
    for (var x = 0; x <= this.props.xval-1; x++){
      for (var y = 0; y <= this.props.yval-1; y++){
        for (var i = min(x-1, 0); i <= max(x+1, this.props.xval-1); i++){
          for (var j = min(y-1, 0); j <= max(y+1, this.props.yval-1); j++){
            if(j !== 0 && i !== 0 && board[i*this.props.xval + j] !=='B'){
              board[(x + i)*this.props.xval + (y + i)] += 1
            }
          }
        }
      }
    }
    return board

  }
  renderBoard(){
    return(
      <Board
        xval = {this.state.xval}
        yval = {this.state.yval}
        board = {Array(this.state.yval*this.state.xval).fill(2)}
      />
    );
  }
  render(){
    return(
      this.renderBoard()
    );
  }
}
function App() {
  return (
    <div className="App">
      <Game
        xval = {9}
        yval = {9}
      />
    </div>
  );
}

export default App;


function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
}
