import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Square(props) {
  return (
    <button className="square" onClick={props.handleClick}>
      {props.value}
    </button>
  );
}

class Board extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      board:Array(this.props.xval * this.props.yval).fill(2),
      shown: Array(this.props.xval * this.props.yval).fill(false),
    };
  }
  generateSquare(value){
    return (<Square
      value = {this.state.shown[value] ? this.props.board[value] : null}
      handleClick = {() => this.handleClick(value)}
    />);
  }
  handleClick(val){
    const curr = this.state.shown.slice()
    curr[val] = true
    console.log(curr)
    this.setState(
      {
        board:this.props.gameboard,
        shown: curr,
      }
    );
  }
  generateBoard(val1, val2){
    var list = [];
    for (var i = 0; i <= val1-1; i++) {
      list.push(i);
    }
    var list2 = [];
    for (var x = 0; x <= val2-1; x++){
      list2.push(x)
    }
    return(
      list2.map((m) => {return(
          <div className = "board-row">
            {list.map((n) => {return this.generateSquare(m*(val1)+n);})}
          </div>
        )}
      )
      )
  }
  render(){
    return(
      this.generateBoard(this.props.xval,this.props.yval)
    );
  }
}

class Game extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      xval:this.props.xval,
      yval:this.props.yval,
      gameboard: this.generateRandomBoard(),
      index:0
    };
  }
  generateRandomBoard(){
    board = Array(this.state.xval*this.state.yval).fill(0)
    bombs = getRandomInt(5, 5 + Math.floor((this.state.xval*this.state.yval)/2))
    while(bombs > 0){
      cRow = getRandomInt(0, this.state.xval)
      cCol = getRandomInt(0, this.state.yval)
      ind = cRow*this.state.xval + cCol
      if(this.state.gameboard[ind] != 'B'){
        board[ind] = 'B'
        bombs -= 1;
      }
    }

    for (var x = 0; x <= this.state.xval-1; x++){
      for (var y = 0; y <= this.state.yval-1; x++){
        for (var i = -1; i <= 1; i++){
          for (var j = -1; j <= 1; j++){
            if(j != 0 && i != 0 && board[(x + i)*this.state.xval + (y + i)] != 'B'){
              board[(x + i)*this.state.xval + (y + i)] += 1
            }
          }
        }
      }
    }
    return board

  }
  renderBoard(){
    return(
      <Board
        xval = {this.state.xval}
        yval = {this.state.yval}
        board = {Array(this.state.yval*this.state.xval).fill(2)}
      />
    );
  }
  render(){
    return(
      this.renderBoard()
    );
  }
}
function App() {
  return (
    <div className="App">
      <Game
        xval = {9}
        yval = {9}
      />
    </div>
  );
}

export default App;


function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min); //The maximum is exclusive and the minimum is inclusive
}
